                    p_cell_reconfig_req->epc_info.epc_params.num_valid_qos_profiles++;
                    p_cell_reconfig_req->ran_info.mac_layer_params.
                        mac_layer_param_drx.num_valid_drx_profiles = drx_reconfig_idx;
