        /* Coverity_96944 Fix Start */
        oam_error_code_et p_err = OAM_ERR_ATTEMPT_SET_FOR_NON_WRITABLE_PARAM_VAL;
        /* Coverity_96944 Fix End */
/*spr 19296 fix start*/
